import React, { Component } from "react";
import Header from "../components/Header";
/*import Section from "../components/Section";*/
/*import Footer from "../components/Footer";*/

function Footer(props) {
		const cnt = props.count;
		const leftCnt = props.inCompleteCnt;
		if (cnt !== 0) {
			return (
				<footer className="todo-app__footer" id="todo-footer">
			        <div className="todo-app__total">
			            {leftCnt} left
			        </div>
			        <ul className="todo-app__view-buttons">
			            <button onClick={e => this.setState({show: 'all'})}>All</button>
			            <button>Active</button>
			            <button>Completed</button>
			        </ul>
			        <div className="todo-app__clean">
			            <button>Clear completed</button>
			        </div>
			    </footer>
				);
		}
		else {
			return null;
		}
	}

class ErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { error: null, errorInfo: null };
  }

  componentDidCatch(error, errorInfo) {
    // Catch errors in any components below and re-render with error message
    this.setState({
      error: error,
      errorInfo: errorInfo
    })
    // You can also log error messages to an error reporting service here
  }

  render() {
    if (this.state.errorInfo) {
      // Error path
      return (
        <div>
          <h2>Something went wrong.</h2>
          <details style={{ whiteSpace: 'pre-wrap' }}>
            {this.state.error && this.state.error.toString()}
            <br />
            {this.state.errorInfo.componentStack}
          </details>
        </div>
      );
    }
    // Normally, just render children
    return this.props.children;
  }
}

function H1(props) {
	const item = props.item;

	if (item.isCompleted) {
		return (
			<h1 className='todo-app__item-detail todo-app__item-detail-completed' >{item.value}</h1>
			);
	}
	else {
		return (
			<h1 className="todo-app__item-detail">{item.value}</h1>
		)
	}
}

class TodoApp extends Component {
	constructor(props) {
		super(props);
		this.state = {
			newItem: '',
			itemList: [],
			count: 0,
			show: 'all', /*all, complete, left*/
		};
	}


	updateInput(key, value) {
		this.setState({[key]: value});
	}

	addItem() {
		const newItem = {
				id: this.state.count,
				value: this.state.newItem.slice(),
				isCompleted: false,
			};

		const itemList = [...this.state.itemList];
		itemList.push(newItem);

		const newCnt = this.state.count + 1;

		this.setState({
			itemList,
			newItem: '',
			count: newCnt,
		});
	}

	changeItemState(id) {
		var itemList = [...this.state.itemList];

		if (itemList[id].isCompleted) {
			itemList[id].isCompleted = false;
		}
		else {
			itemList[id].isCompleted = true;
		}

		this.setState({
			itemList,
		});
	}

	deleteItem(id) {
		const itemList = [...this.state.itemList];
		const updatedList = itemList.filter(item => item.id !== id);
		const newCnt = this.state.count - 1;
		this.setState({
			itemList: updatedList,
			count: newCnt,
		})
	}

    render() {
        return (
            <>
                <Header text="todos" />
                <section className="todo-app__main">
	                <input
	                	className="todo-app__input"
	                	placeholder="What needs to be done?"
	                	value={this.state.newItem}
	                	onChange={e => this.updateInput("newItem", e.target.value)}
	                	onKeyPress={e => {if (e.key === 'Enter') {this.addItem();}}} />
	                <ul className="todo-app__list" id="todo-list">
	                	{this.state.itemList.map(item => {
	                		return (
	                			<li key={item.id} className="todo-app__item">
			                        <div className="todo-app__checkbox">
			                            <input
			                            	id={item.id}
			                            	type='checkbox'
			                            	onClick={e => this.changeItemState(item.id)}/>
			                            <label htmlFor={item.id}></label>
			                        </div>
			                        <ErrorBoundary>
									  						<H1 item={item} />
															</ErrorBoundary>

			                        <img src="img/x.png" className="todo-app__item-x" onClick={e => this.deleteItem(item.id)}/>
			                    </li>
	                			);
	                	})}
	                </ul>
            	</section>
	            <Footer count={this.state.count} inCompleteCnt={this.state.itemList.filter(item => item.isCompleted !== true).length} />
            </>
        );
    }
}

export default TodoApp;
