import React, { Component } from "react";
import Header from "../components/Header";
/*import Section from "../components/Section";*/
/*import Footer from "../components/Footer";*/

function Footer(props) {
		const cnt = props.count;
		if (cnt !== 0) {
			return (
				<footer className="todo-app__footer" id="todo-footer">
			        <div className="todo-app__total">
			            {cnt} left
			        </div>
			        <ul className="todo-app__view-buttons">
			            <button>All</button>
			            <button>Active</button>
			            <button>Completed</button>                        
			        </ul>
			        <div className="todo-app__clean">
			            <button>Clear completed</button>
			        </div>
			    </footer>
				);
		}
		else {
			return null;
		}
	}

class ErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hasError: false };
  }

  static getDerivedStateFromError(error) {
    // 更新 state 以至於下一個 render 會顯示 fallback UI
    return { hasError: true };
  }

  componentDidCatch(error, errorInfo) {
    // 你也可以把錯誤記錄到一個錯誤回報系統服務
    logErrorToMyService(error, errorInfo);
  }

  render() {
  	if (this.state.hasError){
    	// 你可以 render 任何客製化的 fallback UI
	      return <h1>Something went wrong.</h1>;
    } 
	    return this.props.children;
  }

}

function H1(props) {
	const item = props.item;
	if (item.isCompleted) {
		return (
			<h1 className="todo-app__item-detail" style="text-decoration: line-through; opacity: 0.5;">{item.value}</h1>
			);
	}
	else {
		return (
			<h1 className="todo-app__item-detail">{item.value}</h1>
		)
	}
}

class TodoApp extends Component {
	constructor(props) {
		super(props);
		this.state = {
			newItem: '',
			itemList: [],
			count: 0,
		};
	}


	updateInput(key, value) {
		this.setState({[key]: value});
	}

	addItem() {
		const newItem = {
				id: this.state.count,
				value: this.state.newItem.slice(),
				isCompleted: false,
			};

		const itemList = [...this.state.itemList];
		itemList.push(newItem);

		const newCnt = this.state.count + 1;

		this.setState({
			itemList,
			newItem: '',
			count: newCnt,
		});
	}

	changeItemState(id) {
		var itemList = [...this.state.itemList];

		if (itemList[id].isCompleted) {
			itemList[id].isCompleted = false;
		}
		else {
			itemList[id].isCompleted = true;
		}

		this.setState({
			itemList,
		});
	}


    render() {
        return (
            <>
                <Header text="todos" />
                <section className="todo-app__main">
	                <input 
	                	className="todo-app__input" 
	                	placeholder="What needs to be done?"
	                	value={this.state.newItem}
	                	onChange={e => this.updateInput("newItem", e.target.value)}
	                	onKeyPress={e => {if (e.key === 'Enter') {this.addItem();}}} />
	                <ul className="todo-app__list" id="todo-list">
	                	{this.state.itemList.map(item => {
	                		return (
	                			<li key={item.id} className="todo-app__item">
			                        <div className="todo-app__checkbox">			                  
			                            <input 
			                            	id={item.id}
			                            	type='checkbox'
			                            	onClick={e => this.changeItemState(item.id)}/>
			                            <label htmlFor={item.id}></label>
			                        </div>
			                        <ErrorBoundary>
									  <H1 item={item} />
									</ErrorBoundary>

			                        <img src="img/x.png" className="todo-app__item-x" />
			                    </li>
	                			);
	                	})}
	                </ul>
            	</section>
	            <Footer count={this.state.count} />
            </>
        );
    }
}

export default TodoApp;
