import React, { Component } from "react";
import Header from "../components/Header";

class TodoApp extends Component {
	constructor(props) {
		super(props);
		this.state = {
			newItem: '',
			itemList: [],
			count: 0,
			show: 'all', /*all, active, completed*/
		};
	}

	showList() {
		 var showList = this.state.itemList;
		 if (this.state.show === 'active') {
			 showList = this.state.itemList.filter(item => item.isCompleted !== true);
		 }
		 else if (this.state.show === 'completed') {
			 showList = this.state.itemList.filter(item => item.isCompleted === true);
		 }
		 const showContent = showList.map(item => {
			return (
				<li key={showList.indexOf(item)} className="todo-app__item">
							<div className="todo-app__checkbox">
									<input
										id={showList.indexOf(item)}
										type='checkbox'
										onClick={e => this.changeItemState(showList.indexOf(item))}/>
									<label htmlFor={showList.indexOf(item)} style={item.isCompleted? {background: '#26ca299b'}:null}></label>
							</div>
								<h1 className={item.isCompleted? 'todo-app__item-detail todo-app__item-detail-completed': 'todo-app__item-detail'} >{item.value}</h1>

							<img src="img/x.png" className="todo-app__item-x" onClick={e => this.deleteItem(showList.indexOf(item))}/>
					</li>
				);
		})
		return showContent;
	}

	addItem() {
		const newItem = {
				value: this.state.newItem.slice(),
				isCompleted: false,
			};

		var updatedList = [...this.state.itemList];
		updatedList.push(newItem);

		const newCnt = this.state.count + 1;

		this.setState({
			itemList: updatedList,
			newItem: '',
			count: newCnt,
		});
	}

	changeItemState(id) {
		var itemList = [...this.state.itemList];

		if (itemList[id].isCompleted) {
			itemList[id].isCompleted = false;
		}
		else {
			itemList[id].isCompleted = true;
		}

		this.setState({
			itemList,
		});
	}

	deleteItem(id) {
		const itemList = [...this.state.itemList];
		var updatedList = itemList.filter(item => itemList.indexOf(item) !== id);
		const newCnt = this.state.count - 1;
		this.setState({
			itemList: updatedList,
			count: newCnt,
		})
		if (newCnt === 0) {
			this.setState({
				show: 'all',
			})
		}
	}

	clearCompleted(){
		var updatedList = this.state.itemList.filter(item => item.isCompleted !== true);
		this.setState({
			itemList: updatedList,
			count: updatedList.length,
		})
		if (this.state.count === 0) {
			this.setState({
				show: 'all',
			})
		}
	}

	getItemNum(){
		const num = this.state.itemList.filter(item => item.isCompleted !== true).length;
		if (num !== 0) {
			return num.toString() + ' incompleted left';
		} else {
			return 'All clear!';
		}
	}

    render() {
        return (
            <>
                <Header text="todos" />
                <section className="todo-app__main">
	                <input
	                	className="todo-app__input"
	                	placeholder="What needs to be done?"
	                	value={this.state.newItem}
	                	onChange={e => this.setState({newItem: e.target.value})}
	                	onKeyPress={e => {if (e.key === 'Enter') {this.addItem();}}} />
	                <ul className="todo-app__list" id="todo-list">
	                	{this.showList()}
	                </ul>
            	</section>
								<footer className="todo-app__footer" id="todo-footer" style={this.state.count === 0? {visibility:'hidden'}: {visibility:'visible'}}>
							        <div className="todo-app__total">
							            {this.getItemNum()}
							        </div>
							        <ul className="todo-app__view-buttons">
							            <button style={this.state.show === 'all'? {border: '1px outset #696969'} :null} onClick={e => this.setState({show: 'all'})}>All</button>
							            <button style={this.state.show === 'active'? {border: '1px outset #696969'} :null} onClick={e => this.setState({show: 'active'})}>Active</button>
							            <button style={this.state.show === 'completed'? {border: '1px outset #696969'} :null} onClick={e => this.setState({show: 'completed'})}>Completed</button>
							        </ul>
							        <div className="todo-app__clean">
							            <button onClick={e => this.clearCompleted()} style={this.state.itemList.filter(item => item.isCompleted === true).length === 0? {visibility:'hidden'}: {visibility:'visible'}}>Clear completed</button>
							        </div>
							    </footer>
            </>
        );
    }
}

export default TodoApp;
