import React from "react";
export default ({count, show, itemList, clearCompleted, updateState}) => {
    /*<footer className="todo-app__footer" id="todo-footer">
        <div className="todo-app__total">
            2 left
        </div>
        <ul className="todo-app__view-buttons">
            <button>All</button>
            <button>Active</button>
            <button>Completed</button>
        </ul>
        <div className="todo-app__clean">
            <button>Clear completed</button>
        </div>
    </footer>*/
    var num = itemList.filter(item => item.isCompleted !== true).length;
    var itemNum = num !== 0? num.toString() + ' incompleted left': 'All clear!';

    return (
      <footer className="todo-app__footer" id="todo-footer" style={count === 0? {visibility:'hidden'}: {visibility:'visible'}}>
            <div className="todo-app__total">
                {this.itemNum}
            </div>
            <ul className="todo-app__view-buttons">
                <button style={show === 'all'? {border: '1px outset #696969'} :null} onClick={e => updateState({show: 'all'})}>All</button>
                <button style={show === 'active'? {border: '1px outset #696969'} :null} onClick={e => updateState({show: 'active'})}>Active</button>
                <button style={show === 'completed'? {border: '1px outset #696969'} :null} onClick={e => updateState({show: 'completed'})}>Completed</button>
            </ul>
            <div className="todo-app__clean">
                <button onClick={e => clearCompleted()} style={itemList.filter(item => item.isCompleted === true).length === 0? {visibility:'hidden'}: {visibility:'visible'}}>Clear completed</button>
            </div>
        </footer>
    );
}
